// NoteList.js
import React from "react";
import { View, Text, Button, StyleSheet } from "react-native";

const NoteList = () => {
  return (
    <View>
      <Text style={styles.header}>GHI CHÚ</Text>
      <View style={styles.noteContainer}>
        <Text style={styles.noteTitle}>Tên ghi chú</Text>
        <Text style={styles.noteContent}>Nội dung ghi chú</Text>
        <Text style={styles.noteDate}>Ngày tháng ghi chú</Text>
      </View>
      <Button
        style={styles.addButton}
        title="+ Mới"
        onPress={() => {
          /* Xử lý khi nhấn nút + mới */
        }}
      />
    </View>
  );
};

const styles = StyleSheet.create({
  header: {
    fontSize: 20,
    fontWeight: "bold",
    textAlign: "center",
  },
  noteContainer: {
    marginTop: 10,
    paddingHorizontal: 20,
  },
  noteTitle: {
    fontSize: 16,
    fontWeight: "bold",
  },
  noteContent: {
    fontSize: 14,
  },
  noteDate: {
    fontSize: 12,
    color: "gray",
  },
  addButton: {
    borderColor: "rgba(255, 255, 255,0)",
    backgroundColor: "red",
  },
});

export default NoteList;
